+ _currentFovEntity = entity;
+ _fovCache.Clear();
+
+ // Simple circular FOV for now
+ int radius = entity.Awareness;
+ int x0 = entity.X;
+ int y0 = entity.Y;
+
+ // Always include the entity's own position
+ _fovCache.Add((x0, y0));
+
+ // Compute FOV in a circular area
+ for (int x = x0 - radius; x <= x0 + radius; x++)
+ {
+ for (int y = y0 - radius; y <= y0 + radius; y++)
+ {
+ if (!IsInBounds(x, y))
+ continue;
+
+ // Check if the position is within the radius
+ float distance = GetDistance(x0, y0, x, y);
+ if (distance > radius)
+ continue;
+
+ // Check if there's a clear line of sight to the position
+ if (HasLineOfSight(x0, y0, x, y))
+ {
+ _fovCache.Add((x, y));
+ }
+ else if (lightWalls && distance <= radius)
+ {
+ // Check if this is a wall adjacent to a visible cell
+ foreach (var dir in GetAdjacentDirections())
+ {
+ var vector = dir.ToVector();
+ int adjX = x + vector.x;
+ int adjY = y + vector.y;
+
+ if (IsInBounds(adjX, adjY) && _fovCache.Contains((adjX, adjY)))
+ {
+ _fovCache.Add((x, y));
+ break;
+ }
+ }
+ }
+ }
+ }
 }

