+ #region Line of Sight
+
+ /// <summary>
+ /// Checks if there's a clear line of sight between two positions
+ /// </summary>
+ /// <param name="x1">X-coordinate of the first position</param>
+ /// <param name="y1">Y-coordinate of the first position</param>
+ /// <param name="x2">X-coordinate of the second position</param>
+ /// <param name="y2">Y-coordinate of the second position</param>
+ /// <returns>True if there's a clear line of sight, false otherwise</returns>
+ private bool HasLineOfSight(int x1, int y1, int x2, int y2)
+ {
+ // Bresenham's line algorithm
+ int dx = Math.Abs(x2 - x1);
+ int dy = Math.Abs(y2 - y1);
+ int sx = x1 < x2 ? 1 : -1;
+ int sy = y1 < y2 ? 1 : -1;
+ int err = dx - dy;
+
+ while (true)
+ {
+ // Check if we've reached the destination
+ if (x1 == x2 && y1 == y2)
+ return true;
+
+ // Check if the current position is transparent
+ if (x1 != x2 || y1 != y2) // Skip the starting position
+ {
+ if (!IsTransparent(x1, y1))
+ return false;
+ }
+
+ int e2 = 2 * err;
+ if (e2 > -dy)
+ {
+ err -= dy;
+ x1 += sx;
+ }
+ if (e2 < dx)
+ {
+ err += dx;
+ y1 += sy;
+ }
+ }
+ }
+
+ /// <summary>
+ /// Gets all adjacent directions (cardinal and diagonal)
+ /// </summary>
+ /// <returns>Array of adjacent directions</returns>
+ private Direction[] GetAdjacentDirections()
+ {
+ return new[]
+ {
+ Direction.Up,
+ Direction.UpRight,
+ Direction.Right,
+ Direction.DownRight,
+ Direction.Down,
+ Direction.DownLeft,
+ Direction.Left,
+ Direction.UpLeft
+ };
+ }
