+using System;
+using SimToolAI.Core.Entities;
+
+namespace SimToolAI.Core.Map
+{
+ /// <summary>
+ /// Represents a collider for an entity
+ /// </summary>
+ public class Collider
+ {
+ /// <summary>
+ /// The entity this collider belongs to
+ /// </summary>
+ public Entity Entity { get; }
+
+ /// <summary>
+ /// The shape of the collider
+ /// </summary>
+ public ColliderShape Shape { get; }
+
+ /// <summary>
+ /// The radius of the collider (for circular colliders)
+ /// </summary>
+ public float Radius { get; }
+
+ /// <summary>
+ /// The width of the collider (for rectangular colliders)
+ /// </summary>
+ public int Width { get; }
+
+ /// <summary>
+ /// The height of the collider (for rectangular colliders)
+ /// </summary>
+ public int Height { get; }
+
+ /// <summary>
+ /// Creates a new circular collider
+ /// </summary>
+ /// <param name="entity">Entity the collider belongs to</param>
+ /// <param name="radius">Radius of the collider</param>
+ public Collider(Entity entity, float radius)
+ {
+ Entity = entity ?? throw new ArgumentNullException(nameof(entity));
+ Shape = ColliderShape.Circle;
+ Radius = radius;
+ }
+
+ /// <summary>
+ /// Creates a new rectangular collider
+ /// </summary>
+ /// <param name="entity">Entity the collider belongs to</param>
+ /// <param name="width">Width of the collider</param>
+ /// <param name="height">Height of the collider</param>
+ public Collider(Entity entity, int width, int height)
+ {
+ Entity = entity ?? throw new ArgumentNullException(nameof(entity));
+ Shape = ColliderShape.Rectangle;
+ Width = width;
+ Height = height;
+ }
+
+ /// <summary>
+ /// Checks if this collider intersects with another collider
+ /// </summary>
+ /// <param name="other">The other collider</param>
+ /// <returns>True if the colliders intersect, false otherwise</returns>
+ public bool Intersects(Collider other)
+ {
+ if (other == null)
+ throw new ArgumentNullException(nameof(other));
+
+ // Circle-Circle collision
+ if (Shape == ColliderShape.Circle && other.Shape == ColliderShape.Circle)
+ {
+ float distance = Entity.DistanceTo(other.Entity);
+ return distance < Radius + other.Radius;
+ }
+
+ // Rectangle-Rectangle collision
+ if (Shape == ColliderShape.Rectangle && other.Shape == ColliderShape.Rectangle)
+ {
+ int x1 = Entity.X - Width / 2;
+ int y1 = Entity.Y - Height / 2;
+ int x2 = other.Entity.X - other.Width / 2;
+ int y2 = other.Entity.Y - other.Height / 2;
+
+ return x1 < x2 + other.Width &&
+ x1 + Width > x2 &&
+ y1 < y2 + other.Height &&
+ y1 + Height > y2;
+ }
+
+ // Circle-Rectangle collision
+ if (Shape == ColliderShape.Circle && other.Shape == ColliderShape.Rectangle)
+ {
+ return CircleRectangleIntersection(this, other);
+ }
+
+ // Rectangle-Circle collision
+ if (Shape == ColliderShape.Rectangle && other.Shape == ColliderShape.Circle)
+ {
+ return CircleRectangleIntersection(other, this);
+ }
+
+ return false;
+ }
+
+ /// <summary>
+ /// Checks if a circle collider intersects with a rectangle collider
+ /// </summary>
+ /// <param name="circle">The circle collider</param>
+ /// <param name="rectangle">The rectangle collider</param>
+ /// <returns>True if the colliders intersect, false otherwise</returns>
+ private bool CircleRectangleIntersection(Collider circle, Collider rectangle)
+ {
+ // Find the closest point on the rectangle to the circle
+ int rectX = rectangle.Entity.X - rectangle.Width / 2;
+ int rectY = rectangle.Entity.Y - rectangle.Height / 2;
+ int closestX = Math.Max(rectX, Math.Min(circle.Entity.X, rectX + rectangle.Width));
+ int closestY = Math.Max(rectY, Math.Min(circle.Entity.Y, rectY + rectangle.Height));
+
+ // Calculate the distance between the circle's center and the closest point
+ int dx = circle.Entity.X - closestX;
+ int dy = circle.Entity.Y - closestY;
+ float distance = (float)Math.Sqrt(dx * dx + dy * dy);
+
+ // Check if the distance is less than the circle's radius
+ return distance < circle.Radius;
+ }
+ }
+
+ /// <summary>
+ /// Enum representing the shape of a collider
+ /// </summary>
+ public enum ColliderShape
+ {
+ /// <summary>
+ /// Circular collider
+ /// </summary>
+ Circle,
+
+ /// <summary>
+ /// Rectangular collider
+ /// </summary>
+ Rectangle
+ }
+}","refToolCallId":"toolu_01V5hKMfsJ5zp4kGJfvTLUWs"},{"type":"text","text":"Now, let's create a spatial partitioning system to efficiently handle collision detection:"},{"type":"toolCall","toolCallId":"toolu_01R7SVFTFqN4WZczrv7EVP2i","statusText":"Change generated","status":"SUCCESS","context":{"files":[{"path":"SimToolAI/Core/Map/SpatialPartitioning.cs","fsPath":"C:\Users\UmaGa\Documents\GitHub\Simulation-Toolkit-AI\SimToolAI\Core\Map\SpatialPartitioning.cs"}]},"isHidden":false},{"type":"codeHunk","lineBasedDiff":"--- SimToolAI/Core/Map/SpatialPartitioning.cs
