+using System;
+using System.Collections.Generic;
+using System.Linq;
+using SimToolAI.Core.Entities;
+
+namespace SimToolAI.Core.Map
+{
+ /// <summary>
+ /// Spatial partitioning system for efficient collision detection
+ /// </summary>
+ public class SpatialPartitioning
+ {
+ /// <summary>
+ /// Size of each cell in the grid
+ /// </summary>
+ private readonly int _cellSize;
+
+ /// <summary>
+ /// The grid of entities
+ /// </summary>
+ private readonly Dictionary<(int x, int y), List<Entity>> _grid = new Dictionary<(int x, int y), List<Entity>>();
+
+ /// <summary>
+ /// Creates a new spatial partitioning system
+ /// </summary>
+ /// <param name="cellSize">Size of each cell in the grid</param>
+ public SpatialPartitioning(int cellSize = 10)
+ {
+ if (cellSize <= 0)
+ throw new ArgumentException("Cell size must be positive", nameof(cellSize));
+
+ _cellSize = cellSize;
+ }
+
+ /// <summary>
+ /// Adds an entity to the grid
+ /// </summary>
+ /// <param name="entity">Entity to add</param>
+ public void AddEntity(Entity entity)
+ {
+ if (entity == null)
+ throw new ArgumentNullException(nameof(entity));
+
+ var cell = GetCell(entity.X, entity.Y);
+ 
+ if (!_grid.TryGetValue(cell, out var entities))
+ {
+ entities = new List<Entity>();
+ _grid[cell] = entities;
+ }
+ 
+ if (!entities.Contains(entity))
+ {
+ entities.Add(entity);
+ }
+ }
+
+ /// <summary>
+ /// Removes an entity from the grid
+ /// </summary>
+ /// <param name="entity">Entity to remove</param>
+ public void RemoveEntity(Entity entity)
+ {
+ if (entity == null)
+ throw new ArgumentNullException(nameof(entity));
+
+ var cell = GetCell(entity.X, entity.Y);
+ 
+ if (_grid.TryGetValue(cell, out var entities))
+ {
+ entities.Remove(entity);
+ 
+ if (entities.Count == 0)
+ {
+ _grid.Remove(cell);
+ }
+ }
+ }
+
+ /// <summary>
+ /// Updates an entity's position in the grid
+ /// </summary>
+ /// <param name="entity">Entity to update</param>
+ /// <param name="oldX">Old X-coordinate</param>
+ /// <param name="oldY">Old Y-coordinate</param>
+ public void UpdateEntity(Entity entity, int oldX, int oldY)
+ {
+ if (entity == null)
+ throw new ArgumentNullException(nameof(entity));
+
+ var oldCell = GetCell(oldX, oldY);
+ var newCell = GetCell(entity.X, entity.Y);
+ 
+ // If the entity hasn't changed cells, we don't need to do anything
+ if (oldCell.Equals(newCell))
+ return;
+ 
+ // Remove the entity from its old cell
+ if (_grid.TryGetValue(oldCell, out var oldEntities))
+ {
+ oldEntities.Remove(entity);
+ 
+ if (oldEntities.Count == 0)
+ {
+ _grid.Remove(oldCell);
+ }
+ }
+ 
+ // Add the entity to its new cell
+ if (!_grid.TryGetValue(newCell, out var newEntities))
+ {
+ newEntities = new List<Entity>();
+ _grid[newCell] = newEntities;
+ }
+ 
+ newEntities.Add(entity);
+ }
+
+ /// <summary>
+ /// Gets all entities at a specific position
+ /// </summary>
+ /// <param name="x">X-coordinate</param>
+ /// <param name="y">Y-coordinate</param>
+ /// <returns>List of entities at the position</returns>
+ public List<Entity> GetEntitiesAt(int x, int y)
+ {
+ var cell = GetCell(x, y);
+ 
+ if (_grid.TryGetValue(cell, out var cellEntities))
+ {
+ return cellEntities.Where(e => e.X == x && e.Y == y).ToList();
+ }
+ 
+ return new List<Entity>();
+ }
+
+ /// <summary>
+ /// Gets all entities in a specific cell
+ /// </summary>
+ /// <param name="cellX">Cell X-coordinate</param>
+ /// <param name="cellY">Cell Y-coordinate</param>
+ /// <returns>List of entities in the cell</returns>
+ public List<Entity> GetEntitiesInCell(int cellX, int cellY)
+ {
+ if (_grid.TryGetValue((cellX, cellY), out var entities))
+ {
+ return entities;
+ }
+ 
+ return new List<Entity>();
+ }
+
+ /// <summary>
+ /// Gets all entities in a specific area
+ /// </summary>
+ /// <param name="minX">Minimum X-coordinate</param>
+ /// <param name="minY">Minimum Y-coordinate</param>
+ /// <param name="maxX">Maximum X-coordinate</param>
+ /// <param name="maxY">Maximum Y-coordinate</param>
+ /// <returns>List of entities in the area</returns>
+ public List<Entity> GetEntitiesInArea(int minX, int minY, int maxX, int maxY)
+ {
+ var result = new List<Entity>();
+ 
+ // Convert world coordinates to cell coordinates
+ int minCellX = minX / _cellSize;
+ int minCellY = minY / _cellSize;
+ int maxCellX = maxX / _cellSize;
+ int maxCellY = maxY / _cellSize;
+ 
+ // Iterate through all cells in the area
+ for (int cellX = minCellX; cellX <= maxCellX; cellX++)
+ {
+ for (int cellY = minCellY; cellY <= maxCellY; cellY++)
+ {
+ if (_grid.TryGetValue((cellX, cellY), out var entities))
+ {
+ // Add entities that are within the specified area
+ foreach (var entity in entities)
+ {
+ if (entity.X >= minX && entity.X <= maxX && entity.Y >= minY && entity.Y <= maxY)
+ {
+ result.Add(entity);
+ }
+ }
+ }
+ }
+ }
+ 
+ return result;
+ }
+
+ /// <summary>
+ /// Gets all entities in a radius around a position
+ /// </summary>
+ /// <param name="x">X-coordinate</param>
+ /// <param name="y">Y-coordinate</param>
+ /// <param name="radius">Radius</param>
+ /// <returns>List of entities in the radius</returns>
+ public List<Entity> GetEntitiesInRadius(int x, int y, float radius)
+ {
+ var result = new List<Entity>();
+ 
+ // Convert world coordinates to cell coordinates
+ int minCellX = (int)((x - radius) / _cellSize);
+ int minCellY = (int)((y - radius) / _cellSize);
+ int maxCellX = (int)((x + radius) / _cellSize);
+ int maxCellY = (int)((y + radius) / _cellSize);
+ 
+ // Iterate through all cells in the area
+ for (int cellX = minCellX; cellX <= maxCellX; cellX++)
+ {
+ for (int cellY = minCellY; cellY <= maxCellY; cellY++)
+ {
+ if (_grid.TryGetValue((cellX, cellY), out var entities))
+ {
+ // Add entities that are within the specified radius
+ foreach (var entity in entities)
+ {
+ float distance = (float)Math.Sqrt(Math.Pow(entity.X - x, 2) + Math.Pow(entity.Y - y, 2));
+ if (distance <= radius)
+ {
+ result.Add(entity);
+ }
+ }
+ }
+ }
+ }
+ 
+ return result;
+ }
+
+ /// <summary>
+ /// Gets the cell for a position
+ /// </summary>
+ /// <param name="x">X-coordinate</param>
+ /// <param name="y">Y-coordinate</param>
+ /// <returns>The cell coordinates</returns>
+ private (int x, int y) GetCell(int x, int y)
+ {
+ return (x / _cellSize, y / _cellSize);
+ }
+
+ /// <summary>
+ /// Clears all entities from the grid
+ /// </summary>
+ public void Clear()
+ {
+ _grid.Clear();
+ }
+ }
