+ public void RegisterEntity(Entity entity)
+ {
+ if (entity == null)
+ throw new ArgumentNullException(nameof(entity));
+
+ // Add entity to spatial partitioning system
+ _spatialPartitioning.AddEntity(entity);
+
+ // Create a default collider for the entity if it doesn't have one
+ if (!_colliders.ContainsKey(entity.Id))
+ {
+ // Default to a circular collider with radius 0.5
