+ /// <param name="entity">Entity to get collider for</param>
+ /// <returns>The entity's collider, or null if not found</returns>
+ public Collider GetCollider(Entity entity)
+ {
+ if (entity == null)
+ throw new ArgumentNullException(nameof(entity));
+
+ if (_colliders.TryGetValue(entity.Id, out var collider))
+ {
+ return collider;
+ }
+
+ return null;
+ }
+
+ /// <summary>
+ /// Checks if an entity collides with any other entity
+ /// </summary>
+ /// <param name="entity">Entity to check</param>
+ /// <returns>True if the entity collides with any other entity, false otherwise</returns>
+ public bool CheckCollision(Entity entity)
+ {
+ if (entity == null)
+ throw new ArgumentNullException(nameof(entity));
+
+ // Get the entity's collider
+ if (!_colliders.TryGetValue(entity.Id, out var collider))
+ return false;
+
+ // Get nearby entities
+ var nearbyEntities = _spatialPartitioning.GetEntitiesInRadius(entity.X, entity.Y, 5);
+
+ // Check for collisions with each nearby entity
+ foreach (var otherEntity in nearbyEntities)
+ {
+ if (otherEntity.Equals(entity))
+ continue;
+
+ if (!_colliders.TryGetValue(otherEntity.Id, out var otherCollider))
+ continue;
+
+ if (collider.Intersects(otherCollider))
+ return true;
+ }
+
+ return false;
 }

 #endregion","refToolCallId":"toolu_01LN3uXTQH19jGv2HaEZFZ8Z"},{"type":"text","text":"Now, let's create an extension method for the Entity class to make it easier to work with the continuous map:"},{"type":"toolCall","toolCallId":"toolu_01UYdAQJ2SmExkN1svdAptNr","statusText":"Change generated","status":"SUCCESS","context":{"files":[{"path":"SimToolAI/Core/Entities/EntityExtensions.cs","fsPath":"C:\Users\UmaGa\Documents\GitHub\Simulation-Toolkit-AI\SimToolAI\Core\Entities\EntityExtensions.cs"}]},"isHidden":false},{"type":"codeHunk","lineBasedDiff":"--- SimToolAI/Core/Entities/EntityExtensions.cs
