+using SimToolAI.Core.Map;
+using SimToolAI.Utilities;
+
+namespace SimToolAI.Core.Entities
+{
+ /// <summary>
+ /// Extension methods for the Entity class
+ /// </summary>
+ public static class EntityExtensions
+ {
+ /// <summary>
+ /// Moves an entity in a direction on a continuous map
+ /// </summary>
+ /// <param name="entity">Entity to move</param>
+ /// <param name="direction">Direction to move in</param>
+ /// <param name="map">Map to move on</param>
+ /// <returns>True if the entity was moved, false otherwise</returns>
+ public static bool MoveInDirection(this Entity entity, Direction direction, ContinuousMap map)
+ {
+ if (entity == null || map == null || direction == Direction.None || direction == Direction.Center)
+ return false;
+
+ // Get the direction vector
+ var vector = direction.ToVector();
+ 
+ // Calculate the new position
+ int newX = entity.X + vector.x;
+ int newY = entity.Y + vector.y;
+ 
+ // Update the entity's facing direction
+ entity.FacingDirection = direction;
+ 
+ // Try to move the entity
+ return map.SetEntityPosition(entity, newX, newY);
+ }
+
+ /// <summary>
+ /// Registers an entity with a continuous map
+ /// </summary>
+ /// <param name="entity">Entity to register</param>
+ /// <param name="map">Map to register with</param>
+ /// <param name="radius">Radius of the entity's collider</param>
+ public static void RegisterWithMap(this Entity entity, ContinuousMap map, float radius = 0.5f)
+ {
+ if (entity == null || map == null)
+ return;
+
+ // Register the entity with the map
+ map.RegisterEntity(entity);
+ 
+ // Set a circular collider for the entity
+ var collider = new Collider(entity, radius);
+ map.SetCollider(entity, collider);
+ }
+
+ /// <summary>
+ /// Unregisters an entity from a continuous map
+ /// </summary>
+ /// <param name="entity">Entity to unregister</param>
+ /// <param name="map">Map to unregister from</param>
+ public static void UnregisterFromMap(this Entity entity, ContinuousMap map)
+ {
+ if (entity == null || map == null)
+ return;
+
+ // Unregister the entity from the map
+ map.UnregisterEntity(entity);
+ }
+ }
