+ /// <summary>
+ /// Adds walls to the map
+ /// </summary>
+ private void AddWalls()
+ {
+ // Add walls around the perimeter
+ for (int x = 0; x < _map.Width; x++)
+ {
+ _map.SetWalkable(x, 0, false);
+ _map.SetWalkable(x, _map.Height - 1, false);
+ }
+
+ for (int y = 0; y < _map.Height; y++)
+ {
+ _map.SetWalkable(0, y, false);
+ _map.SetWalkable(_map.Width - 1, y, false);
+ }
+
+ // Add some random walls
+ var random = new Random();
+ for (int i = 0; i < 50; i++)
+ {
+ int x = random.Next(1, _map.Width - 1);
+ int y = random.Next(1, _map.Height - 1);
+
+ // Don't place walls on the player
+ if (Math.Abs(x - _player.X) <= 2 && Math.Abs(y - _player.Y) <= 2)
+ continue;
+
+ _map.SetWalkable(x, y, false);
+ }
+ }
+
+ /// <summary>
+ /// Adds enemies to the map
+ /// </summary>
+ private void AddEnemies()
+ {
+ var random = new Random();
+
+ // Add some enemies
+ for (int i = 0; i < 5; i++)
+ {
+ // Find a random walkable location
+ var location = _map.GetRandomWalkableLocation();
+ if (location == null)
+ continue;
+
+ // Create an enemy
+ var enemy = new Player($"Enemy {i + 1}", location.Value.x, location.Value.y, 5);
+ enemy.Avatar = new ConsoleEntityRenderable('E', ConsoleColor.Red, ConsoleColor.Black, enemy);
+
+ // Register the enemy with the map
+ enemy.RegisterWithMap(_map);
+
+ // Add the enemy to the scene
+ _scene.AddEntity(enemy);
+ }
+ }
+
+ /// <summary>
+ /// Runs the example
+ /// </summary>
+ public void Run()
+ {
+ Console.Clear();
+ Console.CursorVisible = false;
+
+ // Render the initial state
+ _scene.Render();
+
+ // Main loop
+ bool running = true;
+ while (running)
