+ break;
+ case ConsoleKey.Spacebar:
+ FireBullet();
+ break;
+ }
+
+ // Render the scene after input
+ _scene.Render();
+ }
+
+ // Update the scene
+ _scene.Update(0.1f);
+
+ // Render the scene
+ _scene.Render();
+
+ // Sleep to avoid high CPU usage
+ System.Threading.Thread.Sleep(50);
+ }
+
+ Console.CursorVisible = true;
+ }
+
+ /// <summary>
