+ /// <summary>
+ /// Renders an ISimMap to the console
+ /// </summary>
+ private void RenderSimMap()
+ {
+ int height = _map.Height;
+ int width = _map.Width;
+
+ // Determine the maximum dimensions to render based on console buffer size
+ int maxWidth = Math.Min(width, Console.BufferWidth);
+ int maxHeight = Math.Min(height, Console.BufferHeight);
+
+ for (int y = 0; y < maxHeight; y++)
+ {
+ for (int x = 0; x < maxWidth; x++)
+ {
+ Console.SetCursorPosition(x, y);
+
+ // Determine the character and color to use
+ char character;
+ ConsoleColor foregroundColor;
+ ConsoleColor backgroundColor;
+
+ if (!_map.IsInBounds(x, y))
+ {
+ // Out of bounds
+ character = ' ';
+ foregroundColor = ConsoleColor.Black;
+ backgroundColor = ConsoleColor.Black;
+ }
+ else if (!_map.IsWalkable(x, y))
+ {
+ // Wall
+ character = '#';
+ foregroundColor = ConsoleColor.White;
+ backgroundColor = ConsoleColor.DarkGray;
+ }
+ else if (_map.IsInFov(x, y))
+ {
+ // Visible floor
+ character = '.';
+ foregroundColor = ConsoleColor.Gray;
+ backgroundColor = ConsoleColor.Black;
+ }
+ else
+ {
+ // Non-visible floor
+ character = ' ';
+ foregroundColor = ConsoleColor.DarkGray;
+ backgroundColor = ConsoleColor.Black;
+ }
+
+ // Set the colors
+ Console.ForegroundColor = foregroundColor;
+ Console.BackgroundColor = backgroundColor;
+
+ // Write the character
+ Console.Write(character);
+ }
+ }
+
+ // Reset the colors
+ Console.ResetColor();
 }
 }
