+using System;
+using System.IO;
+using System.Threading;
+using SimToolAI.Core.Entities;
+using SimToolAI.Core.Map;
+using SimToolAI.Core.Rendering;
+using SimToolAI.Core.Rendering.RenderStrategies;
+using SimToolAI.Utilities;
+
+namespace SimToolAI.Examples
+{
+ /// <summary>
+ /// Example demonstrating the use of the grid map
+ /// </summary>
+ public class GridMapExample
+ {
+ /// <summary>
+ /// Map parser for loading maps from files
+ /// </summary>
+ private readonly GridMapParser<GridMap> _mapParser;
+
+ /// <summary>
+ /// Current map
+ /// </summary>
+ private readonly GridMap _map;
+
+ /// <summary>
+ /// Current scene
+ /// </summary>
+ private readonly ConsoleScene _scene;
+
+ /// <summary>
+ /// Player entity
+ /// </summary>
+ private readonly Player _player;
+
+ /// <summary>
+ /// Game timer for continuous updates
+ /// </summary>
+ private readonly System.Timers.Timer _updateTimer;
+
+ /// <summary>
+ /// Last update time for calculating delta time
+ /// </summary>
+ private DateTime _lastUpdateTime;
+
+ /// <summary>
+ /// Whether the game is running
+ /// </summary>
+ private bool _running;
+
+ /// <summary>
+ /// Creates a new grid map example
+ /// </summary>
+ /// <param name="mapPath">Path to the map file</param>
+ public GridMapExample(string mapPath = null)
+ {
+ // Load the map
+ _mapParser = new GridMapParser<GridMap>();
+ _map = _mapParser.LoadMapFromFile(Path.Exists(mapPath) ? mapPath : "NULL");
+ _map.Initialize(new ConsoleMapRenderable(_mapParser.GetMapGrid(), _map.Height, _map.Width));
+
+ // Create the scene
+ _scene = new ConsoleScene(_map);
+
+ // Create the player
+ _player = new Player("Player", 5, 2, 15)
+ {
+ Health = 100,
+ MaxHealth = 100,
+ AttackPower = 10,
+ Defense = 5,
+ Speed = 1.0f,
+ FacingDirection = Direction.Right
+ };
+
+ // Create the player's avatar
+ _player.Avatar = new ConsoleEntityRenderable('@', ConsoleColor.Yellow, ConsoleColor.Black, _player);
+
+ // Add the player to the scene
+ _scene.AddEntity(_player);
+ _map.ToggleFieldOfView(_player);
+
+ // Initialize the update timer
+ _lastUpdateTime = DateTime.Now;
+ _updateTimer = new System.Timers.Timer(50); // 50ms = 20 updates per second
+ _updateTimer.Elapsed += OnUpdateTimerElapsed;
+ }
+
+ /// <summary>
+ /// Runs the example
+ /// </summary>
+ public void Run()
+ {
+ try
+ {
+ // Display instructions
+ DisplayInstructions();
+
+ // Start the game loop
+ RunGameLoop();
+ }
+ catch (Exception ex)
+ {
+ Console.Clear();
+ Console.ForegroundColor = ConsoleColor.Red;
+ Console.WriteLine("An error occurred:");
+ Console.WriteLine(ex.Message);
+ Console.ResetColor();
+ }
+ finally
+ {
+ CleanupGame();
+ }
+ }
+
+ /// <summary>
+ /// Displays game instructions
+ /// </summary>
+ private void DisplayInstructions()
+ {
+ Console.WriteLine("Grid Map Example");
+ Console.WriteLine("---------------");
+ Console.WriteLine("Controls:");
+ Console.WriteLine("WASD / Arrow Keys: Move player");
+ Console.WriteLine("Spacebar: Fire bullet");
+ Console.WriteLine("Q / Escape: Quit");
+ Console.WriteLine();
+ Console.WriteLine("Press any key to start...");
+ Console.ReadKey(true);
+ Console.Clear();
+ }
+
+ /// <summary>
+ /// Runs the main game loop
+ /// </summary>
+ private void RunGameLoop()
+ {
+ _running = true;
+ _updateTimer.Start();
+
+ while (_running)
+ {
+ ProcessInput();
+ Thread.Sleep(10); // Small delay to prevent CPU hogging
+ }
+ }
+
+ /// <summary>
+ /// Processes user input
+ /// </summary>
+ private void ProcessInput()
+ {
+ // Check if a key is available to avoid blocking
+ if (!Console.KeyAvailable)
+ return;
+
+ ConsoleKey key = Console.ReadKey(true).Key;
+ bool didPlayerAct = false;
+
+ switch (key)
+ {
+ case ConsoleKey.Escape:
+ case ConsoleKey.Q:
+ _running = false;
+ break;
+
+ case ConsoleKey.UpArrow:
+ case ConsoleKey.W:
+ didPlayerAct = CommandSystem.MovePlayer(Direction.Up, _player, _map);
+ break;
+
+ case ConsoleKey.DownArrow:
+ case ConsoleKey.S:
+ didPlayerAct = CommandSystem.MovePlayer(Direction.Down, _player, _map);
+ break;
+
+ case ConsoleKey.LeftArrow:
+ case ConsoleKey.A:
+ didPlayerAct = CommandSystem.MovePlayer(Direction.Left, _player, _map);
+ break;
+
+ case ConsoleKey.RightArrow:
+ case ConsoleKey.D:
+ didPlayerAct = CommandSystem.MovePlayer(Direction.Right, _player, _map);
+ break;
+
+ case ConsoleKey.Spacebar:
+ // Fire a bullet using the command system
+ CommandSystem.FireBullet(_player, _scene, 50);
+ didPlayerAct = true;
+ break;
+ }
+
+ if (didPlayerAct)
+ {
+ _scene.QueryScene<bool>("SetRenderRequired", true);
+ }
+ }
+
+ /// <summary>
+ /// Called when the update timer elapses
+ /// </summary>
+ private void OnUpdateTimerElapsed(object sender, System.Timers.ElapsedEventArgs e)
+ {
+ // Calculate deltaTime in seconds
+ DateTime currentTime = DateTime.Now;
+ float deltaTime = (float)(currentTime - _lastUpdateTime).TotalSeconds;
+ _lastUpdateTime = currentTime;
+
+ // Update the scene with the proper deltaTime
+ _scene.Update(deltaTime);
+
+ // Only render if needed
+ if ((bool)_scene.QueryScene<object>("IsRenderRequired"))
+ {
+ _scene.Render();
+ }
+ }
+
+ /// <summary>
+ /// Cleans up game resources
+ /// </summary>
+ private void CleanupGame()
+ {
+ // Stop the update timer
+ if (_updateTimer != null)
+ {
+ _updateTimer.Stop();
+ _updateTimer.Dispose();
+ }
+
+ // Display exit message
+ Console.Clear();
+ Console.WriteLine("Grid Map Example ended. Thanks for playing!");
+ }
+
+ /// <summary>
+ /// Entry point for the example
+ /// </summary>
+ public static void Main(string mapPath = null)
+ {
+ var example = new GridMapExample(mapPath);
+ example.Run();
+ }
+ }
+}","refToolCallId":"toolu_01MEU8Jr1SVWgDaALX5ZJgZZ"},{"type":"text","text":"Now let's update the Program.cs file to use our new GridMapExample class:"},{"type":"toolCall","toolCallId":"toolu_01RL8wF5XfLEXGhpVStWzWmS","statusText":"Change generated","status":"SUCCESS","context":{"files":[{"path":"SimToolAI/Program.cs","fsPath":"C:\Users\UmaGa\Documents\GitHub\Simulation-Toolkit-AI\SimToolAI\Program.cs"}]},"isHidden":false},{"type":"codeHunk","lineBasedDiff":"--- SimToolAI/Program.cs
