+using System;
+using SimToolAI.Core.Rendering;
+using SimToolAI.Core.Rendering.RenderStrategies;
+using SimToolAI.Utilities;
+
+namespace SimToolAI.Core.Entities
+{
+ /// <summary>
+ /// Represents an enemy entity in the simulation
+ /// </summary>
+ public class Enemy : Entity
+ {
+ #region Properties
+
+ /// <summary>
+ /// Gets or sets the enemy's health
+ /// </summary>
+ public int Health { get; set; } = 50;
+
+ /// <summary>
+ /// Gets or sets the enemy's maximum health
+ /// </summary>
+ public int MaxHealth { get; set; } = 50;
+
+ /// <summary>
+ /// Gets or sets the enemy's attack power
+ /// </summary>
+ public int AttackPower { get; set; } = 5;
+
+ /// <summary>
+ /// Gets or sets the enemy's defense
+ /// </summary>
+ public int Defense { get; set; } = 2;
+
+ /// <summary>
+ /// Gets whether the enemy is alive
+ /// </summary>
+ public bool IsAlive => Health > 0;
+
+ /// <summary>
+ /// Reference to the scene
+ /// </summary>
+ private readonly Scene _scene;
+
+ #endregion
+
+ #region Constructors
+
+ /// <summary>
+ /// Creates a new enemy with the specified parameters
+ /// </summary>
+ /// <param name="name">Name of the enemy</param>
+ /// <param name="x">X-coordinate</param>
+ /// <param name="y">Y-coordinate</param>
+ /// <param name="awareness">Awareness radius</param>
+ /// <param name="scene">Scene reference</param>
+ public Enemy(string name, int x, int y, int awareness, Scene scene) : base(name, x, y, awareness)
+ {
+ _scene = scene ?? throw new ArgumentNullException(nameof(scene));
+ 
+ // Create the enemy's avatar
+ Avatar = new ConsoleEntityRenderable('E', ConsoleColor.Red, ConsoleColor.Black, this);
+ }
+
+ /// <summary>
+ /// Creates a new enemy with the specified parameters and default awareness
+ /// </summary>
+ /// <param name="name">Name of the enemy</param>
+ /// <param name="x">X-coordinate</param>
+ /// <param name="y">Y-coordinate</param>
+ /// <param name="scene">Scene reference</param>
+ public Enemy(string name, int x, int y, Scene scene) : base(name, x, y, 8)
+ {
+ _scene = scene ?? throw new ArgumentNullException(nameof(scene));
+ 
+ // Create the enemy's avatar
+ Avatar = new ConsoleEntityRenderable('E', ConsoleColor.Red, ConsoleColor.Black, this);
+ }
+
+ #endregion
+
+ #region Methods
+
+ /// <summary>
+ /// Updates the enemy state
+ /// </summary>
+ /// <param name="deltaTime">Time elapsed since the last update in seconds</param>
+ public override void Update(float deltaTime)
+ {
+ // If the enemy is dead, remove it from the scene
+ if (!IsAlive)
+ {
+ _scene.RemoveEntity(this);
+ return;
+ }
+
+ // For now, the enemy just stands still
+ // Future implementations could add movement, attacking, etc.
+ }
+
+ /// <summary>
+ /// Applies damage to the enemy
+ /// </summary>
+ /// <param name="amount">Amount of damage to apply</param>
+ /// <returns>True if the enemy was damaged, false if the enemy died</returns>
+ public bool TakeDamage(int amount)
+ {
+ if (!IsAlive)
+ return false;
+
+ // Apply defense reduction
+ int actualDamage = Math.Max(1, amount - Defense);
+
+ Health = Math.Max(0, Health - actualDamage);
+
+ // If the enemy died, trigger a render update
+ if (!IsAlive)
+ {
+ _scene.QueryScene<bool>("SetRenderRequired", true);
+ }
+
+ return IsAlive;
+ }
+
+ /// <summary>
+ /// Returns a string representation of this enemy
+ /// </summary>
+ /// <returns>A string representation of this enemy</returns>
+ public override string ToString()
+ {
+ return $"{Name} (HP: {Health}/{MaxHealth})";
+ }
+
+ #endregion
+ }
