+using System;
+
+namespace SimToolAI.Utilities
+{
+ /// <summary>
+ /// Represents a 3D vector with X, Y, and Z components
+ /// </summary>
+ [Serializable]
+ public struct Vector3 : IEquatable<Vector3>
+ {
+ #region Static Properties
+ 
+ /// <summary>
+ /// Vector with all components set to zero (0, 0, 0)
+ /// </summary>
+ public static Vector3 Zero => new Vector3(0, 0, 0);
+ 
+ /// <summary>
+ /// Vector pointing up (0, -1, 0)
+ /// </summary>
+ public static Vector3 Up => new Vector3(0, -1, 0);
+ 
+ /// <summary>
+ /// Vector pointing down (0, 1, 0)
+ /// </summary>
+ public static Vector3 Down => new Vector3(0, 1, 0);
+ 
+ /// <summary>
+ /// Vector pointing left (-1, 0, 0)
+ /// </summary>
+ public static Vector3 Left => new Vector3(-1, 0, 0);
+ 
+ /// <summary>
+ /// Vector pointing right (1, 0, 0)
+ /// </summary>
+ public static Vector3 Right => new Vector3(1, 0, 0);
+ 
+ /// <summary>
+ /// Vector pointing forward (0, 0, 1)
+ /// </summary>
+ public static Vector3 Forward => new Vector3(0, 0, 1);
+ 
+ /// <summary>
+ /// Vector pointing backward (0, 0, -1)
+ /// </summary>
+ public static Vector3 Backward => new Vector3(0, 0, -1);
+ 
+ /// <summary>
+ /// Vector pointing up-left (-1, -1, 0)
+ /// </summary>
+ public static Vector3 UpLeft => new Vector3(-1, -1, 0);
+ 
+ /// <summary>
+ /// Vector pointing up-right (1, -1, 0)
+ /// </summary>
+ public static Vector3 UpRight => new Vector3(1, -1, 0);
+ 
+ /// <summary>
+ /// Vector pointing down-left (-1, 1, 0)
+ /// </summary>
+ public static Vector3 DownLeft => new Vector3(-1, 1, 0);
+ 
+ /// <summary>
+ /// Vector pointing down-right (1, 1, 0)
+ /// </summary>
+ public static Vector3 DownRight => new Vector3(1, 1, 0);
+ 
+ #endregion
+ 
+ #region Properties
+ 
+ /// <summary>
+ /// X component of the vector
+ /// </summary>
+ public float X { get; set; }
+ 
+ /// <summary>
+ /// Y component of the vector
+ /// </summary>
+ public float Y { get; set; }
+ 
+ /// <summary>
+ /// Z component of the vector
+ /// </summary>
+ public float Z { get; set; }
+ 
+ /// <summary>
+ /// Gets the magnitude (length) of the vector
+ /// </summary>
+ public float Magnitude => (float)Math.Sqrt(X * X + Y * Y + Z * Z);
+ 
+ /// <summary>
+ /// Gets the squared magnitude of the vector (faster than Magnitude)
+ /// </summary>
+ public float SqrMagnitude => X * X + Y * Y + Z * Z;
+ 
+ /// <summary>
+ /// Gets a normalized version of the vector (unit vector)
+ /// </summary>
+ public Vector3 Normalized
+ {
+ get
+ {
+ float mag = Magnitude;
+ if (mag > 0)
+ {
+ return new Vector3(X / mag, Y / mag, Z / mag);
+ }
+ return Zero;
+ }
+ }
+ 
+ #endregion
+ 
+ #region Constructors
+ 
+ /// <summary>
+ /// Creates a new vector with the specified components
+ /// </summary>
+ /// <param name="x">X component</param>
+ /// <param name="y">Y component</param>
+ /// <param name="z">Z component</param>
+ public Vector3(float x, float y, float z = 0)
+ {
+ X = x;
+ Y = y;
+ Z = z;
+ }
+ 
+ /// <summary>
+ /// Creates a new vector from a tuple
+ /// </summary>
+ /// <param name="tuple">Tuple containing X and Y components</param>
+ public Vector3((float x, float y) tuple) : this(tuple.x, tuple.y, 0) { }
+ 
+ /// <summary>
+ /// Creates a new vector from a tuple
+ /// </summary>
+ /// <param name="tuple">Tuple containing X, Y, and Z components</param>
+ public Vector3((float x, float y, float z) tuple) : this(tuple.x, tuple.y, tuple.z) { }
+ 
+ /// <summary>
+ /// Creates a new vector from a Direction enum
+ /// </summary>
+ /// <param name="direction">Direction to convert</param>
+ public Vector3(Direction direction)
+ {
+ var (x, y) = direction.ToVector();
+ X = x;
+ Y = y;
+ Z = 0;
+ }
+ 
+ #endregion
+ 
+ #region Methods
+ 
+ /// <summary>
+ /// Normalizes this vector (makes it a unit vector)
+ /// </summary>
+ public void Normalize()
+ {
+ float mag = Magnitude;
+ if (mag > 0)
+ {
+ X /= mag;
+ Y /= mag;
+ Z /= mag;
+ }
+ }
+ 
+ /// <summary>
+ /// Calculates the dot product of this vector and another vector
+ /// </summary>
+ /// <param name="other">The other vector</param>
+ /// <returns>The dot product</returns>
+ public float Dot(Vector3 other)
+ {
+ return X * other.X + Y * other.Y + Z * other.Z;
+ }
+ 
+ /// <summary>
+ /// Calculates the cross product of this vector and another vector
+ /// </summary>
+ /// <param name="other">The other vector</param>
+ /// <returns>The cross product</returns>
+ public Vector3 Cross(Vector3 other)
+ {
+ return new Vector3(
+ Y * other.Z - Z * other.Y,
+ Z * other.X - X * other.Z,
+ X * other.Y - Y * other.X
+ );
+ }
+ 
+ /// <summary>
+ /// Calculates the distance between this vector and another vector
+ /// </summary>
+ /// <param name="other">The other vector</param>
+ /// <returns>The distance</returns>
+ public float Distance(Vector3 other)
+ {
+ float dx = X - other.X;
+ float dy = Y - other.Y;
+ float dz = Z - other.Z;
+ return (float)Math.Sqrt(dx * dx + dy * dy + dz * dz);
+ }
+ 
+ /// <summary>
+ /// Converts this vector to a Direction enum
+ /// </summary>
+ /// <returns>The closest Direction enum value</returns>
+ public Direction ToDirection()
+ {
+ // Normalize the vector to get a unit vector
+ Vector3 normalized = Normalized;
+ 
+ // Round the components to get the closest direction
+ int x = Math.Abs(normalized.X) < 0.5f ? 0 : (normalized.X > 0 ? 1 : -1);
+ int y = Math.Abs(normalized.Y) < 0.5f ? 0 : (normalized.Y > 0 ? 1 : -1);
+ 
+ // Map the rounded components to a Direction enum value
+ return (x, y) switch
+ {
+ (0, 0) => Direction.None,
+ (-1, 1) => Direction.DownLeft,
+ (0, 1) => Direction.Down,
+ (1, 1) => Direction.DownRight,
+ (-1, 0) => Direction.Left,
+ (0, 0) => Direction.Center,
+ (1, 0) => Direction.Right,
+ (-1, -1) => Direction.UpLeft,
+ (0, -1) => Direction.Up,
+ (1, -1) => Direction.UpRight,
+ _ => Direction.None
+ };
+ }
+ 
+ /// <summary>
+ /// Returns a tuple representation of this vector
+ /// </summary>
+ /// <returns>A tuple containing the X and Y components</returns>
+ public (float x, float y) ToTuple2D()
+ {
+ return (X, Y);
+ }
+ 
+ /// <summary>
+ /// Returns a tuple representation of this vector
+ /// </summary>
+ /// <returns>A tuple containing the X, Y, and Z components</returns>
+ public (float x, float y, float z) ToTuple3D()
+ {
+ return (X, Y, Z);
+ }
+ 
+ /// <summary>
+ /// Returns a string representation of this vector
+ /// </summary>
+ /// <returns>A string representation of this vector</returns>
+ public override string ToString()
+ {
+ return $"({X}, {Y}, {Z})";
+ }
+ 
+ #endregion
+ 
+ #region Operators
+ 
+ /// <summary>
+ /// Adds two vectors
+ /// </summary>
+ /// <param name="a">First vector</param>
+ /// <param name="b">Second vector</param>
+ /// <returns>The sum of the two vectors</returns>
+ public static Vector3 operator +(Vector3 a, Vector3 b)
+ {
+ return new Vector3(a.X + b.X, a.Y + b.Y, a.Z + b.Z);
+ }
+ 
+ /// <summary>
+ /// Subtracts one vector from another
+ /// </summary>
+ /// <param name="a">First vector</param>
+ /// <param name="b">Second vector</param>
+ /// <returns>The difference of the two vectors</returns>
+ public static Vector3 operator -(Vector3 a, Vector3 b)
+ {
+ return new Vector3(a.X - b.X, a.Y - b.Y, a.Z - b.Z);
+ }
+ 
+ /// <summary>
+ /// Negates a vector
+ /// </summary>
+ /// <param name="a">Vector to negate</param>
+ /// <returns>The negated vector</returns>
+ public static Vector3 operator -(Vector3 a)
+ {
+ return new Vector3(-a.X, -a.Y, -a.Z);
+ }
+ 
+ /// <summary>
+ /// Multiplies a vector by a scalar
+ /// </summary>
+ /// <param name="a">Vector to multiply</param>
+ /// <param name="d">Scalar to multiply by</param>
+ /// <returns>The scaled vector</returns>
+ public static Vector3 operator *(Vector3 a, float d)
+ {
+ return new Vector3(a.X * d, a.Y * d, a.Z * d);
+ }
+ 
+ /// <summary>
+ /// Multiplies a scalar by a vector
+ /// </summary>
+ /// <param name="d">Scalar to multiply by</param>
+ /// <param name="a">Vector to multiply</param>
+ /// <returns>The scaled vector</returns>
+ public static Vector3 operator *(float d, Vector3 a)
+ {
+ return new Vector3(a.X * d, a.Y * d, a.Z * d);
+ }
+ 
+ /// <summary>
+ /// Divides a vector by a scalar
+ /// </summary>
+ /// <param name="a">Vector to divide</param>
+ /// <param name="d">Scalar to divide by</param>
+ /// <returns>The scaled vector</returns>
+ public static Vector3 operator /(Vector3 a, float d)
+ {
+ return new Vector3(a.X / d, a.Y / d, a.Z / d);
+ }
+ 
+ /// <summary>
+ /// Checks if two vectors are equal
+ /// </summary>
+ /// <param name="a">First vector</param>
+ /// <param name="b">Second vector</param>
+ /// <returns>True if the vectors are equal, false otherwise</returns>
+ public static bool operator ==(Vector3 a, Vector3 b)
+ {
+ return a.Equals(b);
+ }
+ 
+ /// <summary>
+ /// Checks if two vectors are not equal
+ /// </summary>
+ /// <param name="a">First vector</param>
+ /// <param name="b">Second vector</param>
+ /// <returns>True if the vectors are not equal, false otherwise</returns>
+ public static bool operator !=(Vector3 a, Vector3 b)
+ {
+ return !a.Equals(b);
+ }
+ 
+ #endregion
+ 
+ #region Equality and Hashing
+ 
+ /// <summary>
+ /// Determines whether the specified object is equal to the current vector
+ /// </summary>
+ /// <param name="obj">The object to compare with the current vector</param>
+ /// <returns>True if the specified object is equal to the current vector, false otherwise</returns>
+ public override bool Equals(object obj)
+ {
+ return obj is Vector3 vector && Equals(vector);
+ }
+ 
+ /// <summary>
+ /// Determines whether the specified vector is equal to the current vector
+ /// </summary>
+ /// <param name="other">The vector to compare with the current vector</param>
+ /// <returns>True if the specified vector is equal to the current vector, false otherwise</returns>
+ public bool Equals(Vector3 other)
+ {
+ return X == other.X && Y == other.Y && Z == other.Z;
+ }
+ 
+ /// <summary>
+ /// Returns a hash code for this vector
+ /// </summary>
+ /// <returns>A hash code for this vector</returns>
+ public override int GetHashCode()
+ {
+ return HashCode.Combine(X, Y, Z);
+ }
