+ {
+ foreach (var entity in scene.GetEntitiesInRadius(target.X, target.Y, AoeRadius))
+ {
+ if (entity is Character nearbyCharacter && !entity.Equals(target) && !entity.Equals(Owner))
+ {
+ // Apply reduced damage to nearby entities
+ int aoeDamage = (int)(Damage * 0.5f);
+ nearbyCharacter.TakeDamage(aoeDamage);
+ }
+ }
+ }
+ 
+ return true;
+ }
+ 
+ return false;
+ }
+ 
+ #endregion
+ }
