+ /// <param name="splashDamage">Whether the weapon has splash damage</param>
+ /// <param name="splashRadius">Radius of the splash damage</param>
+ public RangedWeapon(string name, int damage, float attackSpeed, int range, float projectileSpeed = 10, 
+ int projectileRange = 50, bool piercing = false, int pierceCount = 0, bool splashDamage = false, int splashRadius = 0)
+ : base(name, damage, attackSpeed, range)
+ {
+ Type = WeaponType.Ranged;
+ ProjectileSpeed = projectileSpeed;
+ ProjectileRange = projectileRange;
+ Piercing = piercing;
+ PierceCount = pierceCount;
+ SplashDamage = splashDamage;
+ SplashRadius = splashRadius;
+ }
