+ /// <param name="scene">Scene reference</param>
+ /// <returns>True if the attack was successful, false otherwise</returns>
+ public override bool Attack(Entity target, Scene scene)
+ {
+ if (Owner == null || target == null || scene == null)
+ return false;
+ 
+ // Check if the weapon is ready to attack
+ if (!IsReady)
+ return false;
+ 
+ // Check if the target is in range
+ if (!IsInRange(target))
+ return false;
+ 
+ // Start the cooldown
+ StartCooldown();
+ 
+ // Fire a bullet at the target
+ Direction direction = Owner.FacingDirection;
+ 
+ // If we have a target, calculate the direction to it
+ if (target != null)
+ {
+ int dx = target.X - Owner.X;
+ int dy = target.Y - Owner.Y;
+ 
+ // Determine the direction based on the relative position
+ if (dx > 0 && dy < 0) direction = Direction.UpRight;
+ else if (dx < 0 && dy < 0) direction = Direction.UpLeft;
+ else if (dx > 0 && dy > 0) direction = Direction.DownRight;
+ else if (dx < 0 && dy > 0) direction = Direction.DownLeft;
+ else if (dx > 0) direction = Direction.Right;
+ else if (dx < 0) direction = Direction.Left;
+ else if (dy > 0) direction = Direction.Down;
+ else if (dy < 0) direction = Direction.Up;
+ }
+ 
+ // Create a bullet with the weapon's properties
+ var bullet = BulletManager.Instance.CreateBullet(
+ Owner.X, 
+ Owner.Y, 
+ direction, 
+ scene, 
+ Owner, 
+ this,
+ ProjectileSpeed, 
+ Damage
+ );
+ 
+ if (bullet != null)
+ {
+ bullet.MaxRange = ProjectileRange;
+ bullet.Piercing = Piercing;
+ bullet.PierceCount = PierceCount;
+ bullet.SplashDamage = SplashDamage;
+ bullet.SplashRadius = SplashRadius;
+ 
+ return true;
+ }
+ 
+ return false;
+ }
