+using System;
+using System.Collections.Generic;
+using SimToolAI.Core.Entities;
+using SimToolAI.Core.Rendering;
+using SimToolAI.Utilities;
+
+namespace SimToolAI.Core.Weapons
+{
+ /// <summary>
+ /// Manages bullet creation and tracking
+ /// </summary>
+ public class BulletManager
+ {
+ #region Singleton
+ 
+ private static BulletManager _instance;
+ 
+ /// <summary>
+ /// Gets the singleton instance of the BulletManager
+ /// </summary>
+ public static BulletManager Instance
+ {
+ get
+ {
+ if (_instance == null)
+ {
+ _instance = new BulletManager();
+ }
+ return _instance;
+ }
+ }
+ 
+ #endregion
+ 
+ #region Properties
+ 
+ /// <summary>
+ /// List of active bullets
+ /// </summary>
+ private readonly List<Bullet> _activeBullets = new List<Bullet>();
+ 
+ /// <summary>
+ /// Gets the count of active bullets
+ /// </summary>
+ public int ActiveBulletCount => _activeBullets.Count;
+ 
+ #endregion
+ 
+ #region Constructors
+ 
+ /// <summary>
+ /// Creates a new BulletManager
+ /// </summary>
+ private BulletManager()
+ {
+ }
+ 
+ #endregion
+ 
+ #region Methods
+ 
+ /// <summary>
+ /// Creates a new bullet
+ /// </summary>
+ /// <param name="x">X-coordinate</param>
+ /// <param name="y">Y-coordinate</param>
+ /// <param name="direction">Direction the bullet will travel</param>
+ /// <param name="scene">Scene to add the bullet to</param>
+ /// <param name="owner">Owner of the bullet</param>
+ /// <param name="weapon">Weapon that fired the bullet</param>
+ /// <param name="speed">Speed of the bullet</param>
+ /// <param name="damage">Damage the bullet deals</param>
+ /// <returns>The created bullet</returns>
+ public Bullet CreateBullet(int x, int y, Direction direction, Scene scene, Entity owner, Weapon weapon, float speed = 10, int damage = 20)
+ {
+ if (scene == null)
+ throw new ArgumentNullException(nameof(scene));
+ 
+ if (owner == null)
+ throw new ArgumentNullException(nameof(owner));
+ 
+ if (weapon == null)
+ throw new ArgumentNullException(nameof(weapon));
+ 
+ // Create a new bullet
+ var bullet = new Bullet(x, y, direction, scene, owner, weapon, speed, damage);
+ bullet.FacingDirection = direction;
+ 
+ // Add the bullet to the scene
+ scene.AddEntity(bullet);
+ 
+ // Add the bullet to the active bullets list
+ _activeBullets.Add(bullet);
+ 
+ // Trigger a render update
+ scene.QueryScene<bool>("SetRenderRequired", true);
+ 
+ // Raise the BulletCreated event
+ OnBulletCreated(new BulletEventArgs(bullet));
+ 
+ return bullet;
+ }
+ 
+ /// <summary>
+ /// Removes a bullet from the active bullets list
+ /// </summary>
+ /// <param name="bullet">Bullet to remove</param>
+ public void RemoveBullet(Bullet bullet)
+ {
+ if (bullet == null)
+ return;
+ 
+ _activeBullets.Remove(bullet);
+ 
+ // Raise the BulletDestroyed event
+ OnBulletDestroyed(new BulletEventArgs(bullet));
+ }
+ 
+ /// <summary>
+ /// Updates all active bullets
+ /// </summary>
+ /// <param name="deltaTime">Time elapsed since the last update in seconds</param>
+ public void Update(float deltaTime)
+ {
+ // Update all active bullets
+ for (int i = _activeBullets.Count - 1; i >= 0; i--)
+ {
+ var bullet = _activeBullets[i];
+ 
+ // If the bullet has been removed from the scene, remove it from the active bullets list
+ if (bullet.Scene == null)
+ {
+ _activeBullets.RemoveAt(i);
+ continue;
+ }
+ 
+ // Update the bullet
+ bullet.Update(deltaTime);
+ }
+ }
+ 
+ #endregion
+ 
+ #region Events
+ 
+ /// <summary>
+ /// Event raised when a bullet is created
+ /// </summary>
+ public event EventHandler<BulletEventArgs> BulletCreated;
+ 
+ /// <summary>
+ /// Event raised when a bullet is destroyed
+ /// </summary>
+ public event EventHandler<BulletEventArgs> BulletDestroyed;
+ 
+ /// <summary>
+ /// Raises the BulletCreated event
+ /// </summary>
+ /// <param name="e">Event arguments</param>
+ protected virtual void OnBulletCreated(BulletEventArgs e)
+ {
+ BulletCreated?.Invoke(this, e);
+ }
+ 
+ /// <summary>
+ /// Raises the BulletDestroyed event
+ /// </summary>
+ /// <param name="e">Event arguments</param>
+ protected virtual void OnBulletDestroyed(BulletEventArgs e)
+ {
+ BulletDestroyed?.Invoke(this, e);
+ }
+ 
+ #endregion
+ }
+ 
+ /// <summary>
+ /// Event arguments for bullet events
+ /// </summary>
+ public class BulletEventArgs : EventArgs
+ {
+ /// <summary>
+ /// The bullet
+ /// </summary>
+ public Bullet Bullet { get; }
+ 
+ /// <summary>
+ /// Creates new bullet event arguments
+ /// </summary>
+ /// <param name="bullet">The bullet</param>
+ public BulletEventArgs(Bullet bullet)
+ {
+ Bullet = bullet;
+ }
+ }
