+ /// <summary>
+ /// Applies splash damage to entities in the specified radius
+ /// </summary>
+ /// <param name="centerX">X-coordinate of the center</param>
+ /// <param name="centerY">Y-coordinate of the center</param>
+ private void ApplySplashDamage(int centerX, int centerY)
+ {
+ // Get all entities in the splash radius
+ var entitiesInRadius = _scene.GetEntitiesInRadius(centerX, centerY, SplashRadius);
+ 
+ // Apply reduced damage to nearby entities
+ foreach (var entity in entitiesInRadius)
+ {
+ if (entity is Character character && !entity.Equals(Owner))
+ {
+ // Skip the entity that was directly hit (already damaged)
+ if (entity.X == centerX && entity.Y == centerY)
+ continue;
+ 
+ // Calculate distance-based damage reduction
+ float distance = entity.DistanceTo(centerX, centerY);
+ float damageMultiplier = 1.0f - (distance / SplashRadius);
+ int splashDamage = (int)(Damage * damageMultiplier * 0.5f);
+ 
+ // Apply the splash damage
+ if (splashDamage > 0)
+ {
+ character.TakeDamage(splashDamage);
+ }
+ }
+ }
+ }
+ 
