+ /// <summary>
+ /// Adds a weapon to the character
+ /// </summary>
+ /// <param name="weapon">Weapon to add</param>
+ public void AddWeapon(Weapon weapon)
+ {
+ if (weapon == null)
+ throw new ArgumentNullException(nameof(weapon));
+ 
+ // Set the owner of the weapon
+ weapon.SetOwner(this);
+ 
+ // Add the weapon to the list
+ _weapons.Add(weapon);
+ 
+ // If this is the first weapon, equip it
+ if (_weapons.Count == 1)
+ {
+ EquipWeapon(0);
+ }
+ 
+ // Raise the weapon added event
+ OnWeaponAdded(new WeaponEventArgs(this, weapon));
+ }
+ 
+ /// <summary>
+ /// Removes a weapon from the character
+ /// </summary>
+ /// <param name="weapon">Weapon to remove</param>
+ /// <returns>True if the weapon was removed, false otherwise</returns>
+ public bool RemoveWeapon(Weapon weapon)
+ {
+ if (weapon == null)
+ throw new ArgumentNullException(nameof(weapon));
+ 
+ // If the weapon is currently equipped, unequip it
+ if (CurrentWeapon == weapon)
+ {
+ CurrentWeapon = null;
+ CurrentWeaponIndex = -1;
+ }
+ 
+ // Remove the weapon from the list
+ bool removed = _weapons.Remove(weapon);
+ 
+ // If we removed the current weapon, equip the first weapon in the list
+ if (removed && CurrentWeapon == null && _weapons.Count > 0)
+ {
+ EquipWeapon(0);
+ }
+ 
+ // Raise the weapon removed event
+ if (removed)
+ {
+ OnWeaponRemoved(new WeaponEventArgs(this, weapon));
+ }
+ 
+ return removed;
+ }
+ 
+ /// <summary>
+ /// Equips a weapon at the specified index
+ /// </summary>
+ /// <param name="index">Index of the weapon to equip</param>
+ /// <returns>True if the weapon was equipped, false otherwise</returns>
+ public bool EquipWeapon(int index)
+ {
+ if (index < 0 || index >= _weapons.Count)
+ return false;
+ 
+ CurrentWeapon = _weapons[index];
+ CurrentWeaponIndex = index;
+ 
+ // Raise the weapon equipped event
+ OnWeaponEquipped(new WeaponEventArgs(this, CurrentWeapon));
+ 
+ return true;
+ }
+ 
+ /// <summary>
+ /// Equips the next weapon in the list
+ /// </summary>
+ /// <returns>True if a weapon was equipped, false otherwise</returns>
+ public bool EquipNextWeapon()
+ {
+ if (_weapons.Count == 0)
+ return false;
+ 
+ int nextIndex = (CurrentWeaponIndex + 1) % _weapons.Count;
+ return EquipWeapon(nextIndex);
+ }
+ 
+ /// <summary>
+ /// Equips the previous weapon in the list
+ /// </summary>
+ /// <returns>True if a weapon was equipped, false otherwise</returns>
+ public bool EquipPreviousWeapon()
+ {
+ if (_weapons.Count == 0)
+ return false;
+ 
+ int prevIndex = (CurrentWeaponIndex - 1 + _weapons.Count) % _weapons.Count;
+ return EquipWeapon(prevIndex);
+ }
