+ /// <summary>
+ /// Configuration for a weapon in a simulation
+ /// </summary>
+ [Serializable]
+ public class WeaponConfig
+ {
+ #region Properties
+ 
+ /// <summary>
+ /// Name of the weapon
+ /// </summary>
+ public string Name { get; set; } = "Weapon";
+ 
+ /// <summary>
+ /// Type of the weapon
+ /// </summary>
+ public WeaponType Type { get; set; } = WeaponType.Melee;
+ 
+ /// <summary>
+ /// Base damage of the weapon
+ /// </summary>
+ public int Damage { get; set; } = 10;
+ 
+ /// <summary>
+ /// Attack speed of the weapon (attacks per second)
+ /// </summary>
+ public float AttackSpeed { get; set; } = 1.0f;
+ 
+ /// <summary>
+ /// Range of the weapon in cells
+ /// </summary>
+ public int Range { get; set; } = 1;
+ 
+ /// <summary>
+ /// Whether the weapon has an area of effect (melee weapons only)
+ /// </summary>
+ public bool AreaOfEffect { get; set; } = false;
+ 
+ /// <summary>
+ /// Radius of the area of effect (melee weapons only)
+ /// </summary>
+ public int AoeRadius { get; set; } = 0;
+ 
+ /// <summary>
+ /// Speed of the projectiles fired by this weapon (ranged weapons only)
+ /// </summary>
+ public float ProjectileSpeed { get; set; } = 10.0f;
+ 
+ /// <summary>
+ /// Maximum range of the projectiles fired by this weapon (ranged weapons only)
+ /// </summary>
+ public int ProjectileRange { get; set; } = 50;
+ 
+ /// <summary>
+ /// Whether the weapon can pierce through targets (ranged weapons only)
+ /// </summary>
+ public bool Piercing { get; set; } = false;
+ 
+ /// <summary>
+ /// Maximum number of targets the projectile can pierce through (ranged weapons only)
+ /// </summary>
+ public int PierceCount { get; set; } = 0;
+ 
+ /// <summary>
+ /// Whether the weapon has splash damage (ranged weapons only)
+ /// </summary>
+ public bool SplashDamage { get; set; } = false;
+ 
+ /// <summary>
+ /// Radius of the splash damage (ranged weapons only)
+ /// </summary>
+ public int SplashRadius { get; set; } = 0;
+ 
+ #endregion
+ 
+ #region Methods
+ 
+ /// <summary>
+ /// Creates a weapon from this configuration
+ /// </summary>
+ /// <returns>The created weapon</returns>
+ public Weapon CreateWeapon()
+ {
+ return Type switch
+ {
+ WeaponType.Melee => new MeleeWeapon(Name, Damage, AttackSpeed, Range, AreaOfEffect, AoeRadius),
+ WeaponType.Ranged => new RangedWeapon(Name, Damage, AttackSpeed, Range, ProjectileSpeed, ProjectileRange, Piercing, PierceCount, SplashDamage, SplashRadius),
+ _ => throw new ArgumentOutOfRangeException(nameof(Type), Type, "Invalid weapon type")
+ };
+ }
+ 
+ #endregion
+ }
