+using System;
+using System.Collections.Generic;
+
+namespace SimToolAI.Core.Events
+{
+ /// <summary>
+ /// Manages simulation events
+ /// </summary>
+ public class EventManager
+ {
+ #region Singleton
+ 
+ private static EventManager _instance;
+ 
+ /// <summary>
+ /// Gets the singleton instance of the EventManager
+ /// </summary>
+ public static EventManager Instance
+ {
+ get
+ {
+ if (_instance == null)
+ {
+ _instance = new EventManager();
+ }
+ return _instance;
+ }
+ }
+ 
+ #endregion
+ 
+ #region Properties
+ 
+ /// <summary>
+ /// Event raised when any simulation event occurs
+ /// </summary>
+ public event EventHandler<SimulationEvent> EventRaised;
+ 
+ /// <summary>
+ /// Dictionary of event handlers for specific event types
+ /// </summary>
+ private readonly Dictionary<SimulationEventType, List<Delegate>> _eventHandlers = new Dictionary<SimulationEventType, List<Delegate>>();
+ 
+ #endregion
+ 
+ #region Constructors
+ 
+ /// <summary>
+ /// Creates a new EventManager
+ /// </summary>
+ private EventManager()
+ {
+ // Initialize the event handlers dictionary
+ foreach (SimulationEventType eventType in Enum.GetValues(typeof(SimulationEventType)))
+ {
+ _eventHandlers[eventType] = new List<Delegate>();
+ }
+ }
+ 
+ #endregion
+ 
+ #region Methods
+ 
+ /// <summary>
+ /// Raises a simulation event
+ /// </summary>
+ /// <param name="simulationEvent">Event to raise</param>
+ public void RaiseEvent(SimulationEvent simulationEvent)
+ {
+ if (simulationEvent == null)
+ throw new ArgumentNullException(nameof(simulationEvent));
+ 
+ // Raise the general event
+ EventRaised?.Invoke(this, simulationEvent);
+ 
+ // Raise the specific event
+ var handlers = _eventHandlers[simulationEvent.Type];
+ foreach (var handler in handlers)
+ {
+ if (handler is EventHandler<SimulationEvent> genericHandler)
+ {
+ genericHandler(this, simulationEvent);
+ }
+ else
+ {
+ // Use reflection to invoke the handler with the correct event type
+ var method = handler.GetType().GetMethod("Invoke");
+ method?.Invoke(handler, new object[] { this, simulationEvent });
+ }
+ }
+ }
+ 
+ /// <summary>
+ /// Subscribes to a specific event type
+ /// </summary>
+ /// <param name="eventType">Type of event to subscribe to</param>
+ /// <param name="handler">Event handler</param>
+ public void Subscribe(SimulationEventType eventType, EventHandler<SimulationEvent> handler)
+ {
+ if (handler == null)
+ throw new ArgumentNullException(nameof(handler));
+ 
+ _eventHandlers[eventType].Add(handler);
+ }
+ 
+ /// <summary>
+ /// Subscribes to a specific event type with a strongly-typed handler
+ /// </summary>
+ /// <typeparam name="T">Type of event</typeparam>
+ /// <param name="eventType">Type of event to subscribe to</param>
+ /// <param name="handler">Event handler</param>
+ public void Subscribe<T>(SimulationEventType eventType, EventHandler<T> handler) where T : SimulationEvent
+ {
+ if (handler == null)
+ throw new ArgumentNullException(nameof(handler));
+ 
+ _eventHandlers[eventType].Add(handler);
+ }
+ 
+ /// <summary>
+ /// Unsubscribes from a specific event type
+ /// </summary>
+ /// <param name="eventType">Type of event to unsubscribe from</param>
+ /// <param name="handler">Event handler</param>
+ public void Unsubscribe(SimulationEventType eventType, EventHandler<SimulationEvent> handler)
+ {
+ if (handler == null)
+ throw new ArgumentNullException(nameof(handler));
+ 
+ _eventHandlers[eventType].Remove(handler);
+ }
+ 
+ /// <summary>
+ /// Unsubscribes from a specific event type with a strongly-typed handler
+ /// </summary>
+ /// <typeparam name="T">Type of event</typeparam>
+ /// <param name="eventType">Type of event to unsubscribe from</param>
+ /// <param name="handler">Event handler</param>
+ public void Unsubscribe<T>(SimulationEventType eventType, EventHandler<T> handler) where T : SimulationEvent
+ {
+ if (handler == null)
+ throw new ArgumentNullException(nameof(handler));
+ 
+ _eventHandlers[eventType].Remove(handler);
+ }
+ 
+ /// <summary>
+ /// Clears all event handlers
+ /// </summary>
+ public void ClearAllHandlers()
+ {
+ foreach (var eventType in _eventHandlers.Keys)
+ {
+ _eventHandlers[eventType].Clear();
+ }
+ 
+ EventRaised = null;
+ }
+ 
+ #endregion
+ }
