+ /// Lock object for thread safety
+ /// </summary>
+ private readonly object _eventQueueLock = new object();
+ 
+ /// <summary>
+ /// Whether the event receiver is initialized
+ /// </summary>
+ private bool _isInitialized;
+ 
+ #endregion
