+ ProcessEvents();
+ }
+ 
+ /// <summary>
+ /// Called when the script is destroyed
+ /// </summary>
+ private void OnDestroy()
+ {
+ Cleanup();
+ }
+ 
+ #endregion
+ 
+ #region Methods
+ 
+ /// <summary>
+ /// Initializes the event receiver
+ /// </summary>
+ public void Initialize()
+ {
+ if (_isInitialized)
+ return;
+ 
+ // Subscribe to all event types
+ EventManager.Instance.EventRaised += OnEventRaised;
+ 
+ _isInitialized = true;
+ }
+ 
+ /// <summary>
+ /// Cleans up the event receiver
+ /// </summary>
+ public void Cleanup()
+ {
+ if (!_isInitialized)
+ return;
+ 
+ // Unsubscribe from all event types
+ EventManager.Instance.EventRaised -= OnEventRaised;
+ 
+ _isInitialized = false;
+ }
+ 
+ /// <summary>
+ /// Called when an event is raised
+ /// </summary>
+ /// <param name="sender">Sender of the event</param>
+ /// <param name="e">Event arguments</param>
+ private void OnEventRaised(object sender, SimulationEvent e)
+ {
+ // Add the event to the queue
+ lock (_eventQueueLock)
+ {
+ _eventQueue.Enqueue(e);
+ }
+ }
+ 
+ /// <summary>
+ /// Processes all events in the queue
+ /// </summary>
+ private void ProcessEvents()
+ {
+ // Process all events in the queue
+ while (true)
+ {
+ SimulationEvent simulationEvent;
+ 
+ lock (_eventQueueLock)
+ {
+ if (_eventQueue.Count == 0)
+ break;
+ 
+ simulationEvent = _eventQueue.Dequeue();
+ }
+ 
+ // Process the event
+ ProcessEvent(simulationEvent);
+ }
+ }
+ 
+ /// <summary>
+ /// Processes a single event
+ /// </summary>
+ /// <param name="simulationEvent">Event to process</param>
+ private void ProcessEvent(SimulationEvent simulationEvent)
+ {
+ switch (simulationEvent.Type)
+ {
+ case SimulationEventType.EntityCreated:
+ OnEntityCreated?.Invoke(this, (EntityCreatedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.EntityMoved:
+ OnEntityMoved?.Invoke(this, (EntityMovedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.EntityRemoved:
+ OnEntityRemoved?.Invoke(this, (EntityRemovedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.EntityDamaged:
+ OnEntityDamaged?.Invoke(this, (EntityDamagedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.EntityHealed:
+ OnEntityHealed?.Invoke(this, (EntityHealedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.EntityDied:
+ OnEntityDied?.Invoke(this, (EntityDiedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.WeaponAdded:
+ OnWeaponAdded?.Invoke(this, (WeaponAddedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.WeaponRemoved:
+ OnWeaponRemoved?.Invoke(this, (WeaponRemovedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.WeaponEquipped:
+ OnWeaponEquipped?.Invoke(this, (WeaponEquippedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.BulletFired:
+ OnBulletFired?.Invoke(this, (BulletFiredEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.BulletHit:
+ OnBulletHit?.Invoke(this, (BulletHitEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.BulletDestroyed:
+ OnBulletDestroyed?.Invoke(this, (BulletDestroyedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.SimulationStarted:
+ OnSimulationStarted?.Invoke(this, (SimulationStartedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.SimulationPaused:
+ OnSimulationPaused?.Invoke(this, (SimulationPausedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.SimulationResumed:
+ OnSimulationResumed?.Invoke(this, (SimulationResumedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.SimulationStopped:
+ OnSimulationStopped?.Invoke(this, (SimulationStoppedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.SimulationStepCompleted:
+ OnSimulationStepCompleted?.Invoke(this, (SimulationStepCompletedEvent)simulationEvent);
+ break;
+ 
+ case SimulationEventType.Modified:
+ OnModified?.Invoke(this, (ModifiedEvent)simulationEvent);
+ break;
+ }
+ }
