+using System;
+using System.Collections.Generic;
+using SimToolAI.Core.Configuration;
+using SimToolAI.Core.Entities;
+using SimToolAI.Core.Events;
+using SimToolAI.Core.Map;
+using SimToolAI.Core.Rendering;
+using SimToolAI.Core.Weapons;
+using UnityEngine;
+
+namespace SimToolAI.Core
+{
+ /// <summary>
+ /// MonoBehaviour that runs a simulation in Unity
+ /// </summary>
+ public class AIToolkitRunner : MonoBehaviour
+ {
+ #region Inspector Properties
+ 
+ [Header("Configuration")]
+ [SerializeField] private TextAsset matchConfigFile;
+ [SerializeField] private bool loadConfigOnStart = true;
+ 
+ [Header("Simulation Settings")]
+ [SerializeField] private bool autoStart = true;
+ [SerializeField] private bool pauseOnStart = false;
+ [SerializeField] private float simulationSpeed = 1.0f;
+ 
+ [Header("References")]
+ [SerializeField] private UnityEventReceiver eventReceiver;
+ 
+ #endregion
+ 
+ #region Properties
+ 
+ /// <summary>
+ /// The simulation instance
+ /// </summary>
+ public Simulation Simulation { get; private set; }
+ 
+ /// <summary>
+ /// The match configuration
+ /// </summary>
+ public MatchConfig MatchConfig { get; private set; }
+ 
+ /// <summary>
+ /// The scene
+ /// </summary>
+ public UnityScene Scene { get; private set; }
+ 
+ /// <summary>
+ /// Whether the simulation is running
+ /// </summary>
+ public bool IsRunning { get; private set; }
+ 
+ /// <summary>
+ /// Whether the simulation is paused
+ /// </summary>
+ public bool IsPaused { get; private set; }
+ 
+ /// <summary>
+ /// The current simulation step
+ /// </summary>
+ public int CurrentStep { get; private set; }
+ 
+ /// <summary>
+ /// The total simulation time
+ /// </summary>
+ public float SimulationTime { get; private set; }
+ 
+ /// <summary>
+ /// Dictionary of entities by ID
+ /// </summary>
+ private readonly Dictionary<Guid, Entity> _entities = new Dictionary<Guid, Entity>();
+ 
+ /// <summary>
+ /// Dictionary of characters by ID
+ /// </summary>
+ private readonly Dictionary<Guid, Character> _characters = new Dictionary<Guid, Character>();
+ 
+ /// <summary>
+ /// Dictionary of bullets by ID
+ /// </summary>
+ private readonly Dictionary<Guid, Bullet> _bullets = new Dictionary<Guid, Bullet>();
+ 
+ #endregion
+ 
+ #region Unity Methods
+ 
+ /// <summary>
+ /// Called when the script instance is being loaded
+ /// </summary>
+ private void Awake()
+ {
+ // Create the event receiver if it doesn't exist
+ if (eventReceiver == null)
+ {
+ eventReceiver = gameObject.AddComponent<UnityEventReceiver>();
+ }
+ 
+ // Initialize the event receiver
+ eventReceiver.Initialize();
+ 
+ // Subscribe to events
+ SubscribeToEvents();
+ }
+ 
+ /// <summary>
+ /// Called when the script is enabled
+ /// </summary>
+ private void Start()
+ {
+ // Load the configuration
+ if (loadConfigOnStart && matchConfigFile != null)
+ {
+ LoadConfigFromTextAsset(matchConfigFile);
+ }
+ 
+ // Start the simulation
+ if (autoStart)
+ {
+ StartSimulation();
+ 
+ if (pauseOnStart)
+ {
+ PauseSimulation();
+ }
+ }
+ }
+ 
+ /// <summary>
+ /// Called every frame
+ /// </summary>
+ private void Update()
+ {
+ // Update the simulation
+ if (IsRunning && !IsPaused && Simulation != null)
+ {
+ float deltaTime = Time.deltaTime * simulationSpeed;
+ Simulation.Update(deltaTime);
+ 
+ // Update the simulation time
+ SimulationTime += deltaTime;
+ 
+ // Increment the step counter
+ CurrentStep++;
+ 
+ // Raise the step completed event
+ EventManager.Instance.RaiseEvent(new SimulationStepCompletedEvent(Simulation.Id, CurrentStep, SimulationTime));
+ }
+ }
+ 
+ /// <summary>
+ /// Called when the script is destroyed
+ /// </summary>
+ private void OnDestroy()
+ {
+ // Unsubscribe from events
+ UnsubscribeFromEvents();
+ 
+ // Clean up the event receiver
+ if (eventReceiver != null)
+ {
+ eventReceiver.Cleanup();
+ }
+ 
+ // Stop the simulation
+ StopSimulation();
+ }
+ 
+ #endregion
+ 
+ #region Methods
