+ /// <summary>
+ /// Loads a match configuration from a TextAsset
+ /// </summary>
+ /// <param name="textAsset">TextAsset containing the configuration</param>
+ public void LoadConfigFromTextAsset(TextAsset textAsset)
+ {
+ if (textAsset == null)
+ throw new ArgumentNullException(nameof(textAsset));
+ 
+ // Determine the file type based on the asset name
+ string extension = System.IO.Path.GetExtension(textAsset.name).ToLower();
+ 
+ // Load the configuration
+ MatchConfig = extension switch
+ {
+ ".json" => JsonUtility.FromJson<MatchConfig>(textAsset.text),
+ ".xml" => throw new NotImplementedException("XML configuration loading is not implemented yet"),
+ _ => throw new NotSupportedException($"Unsupported file extension: {extension}")
+ };
+ 
+ Debug.Log($"Loaded match configuration: {MatchConfig.Name}");
+ }
+ 
+ /// <summary>
+ /// Loads a match configuration from a file
+ /// </summary>
+ /// <param name="path">Path to the configuration file</param>
+ public void LoadConfigFromFile(string path)
+ {
+ if (string.IsNullOrEmpty(path))
+ throw new ArgumentNullException(nameof(path));
+ 
+ // Load the configuration
+ MatchConfig = MatchConfig.LoadFromFile(path);
+ 
+ Debug.Log($"Loaded match configuration: {MatchConfig.Name}");
+ }
+ 
+ /// <summary>
+ /// Starts the simulation
+ /// </summary>
+ public void StartSimulation()
+ {
+ if (IsRunning)
+ return;
+ 
+ if (MatchConfig == null)
+ {
+ Debug.LogError("Cannot start simulation: No match configuration loaded");
+ return;
+ }
+ 
+ // Create the map
+ ISimMap map = null;
+ if (!string.IsNullOrEmpty(MatchConfig.MapPath))
+ {
+ try
+ {
+ map = GridMapParser.ParseFromFile(MatchConfig.MapPath);
+ }
+ catch (Exception ex)
+ {
+ Debug.LogError($"Failed to load map: {ex.Message}");
+ return;
+ }
+ }
+ else
+ {
+ // Create a default map
+ map = new GridMap(50, 50);
+ }
+ 
+ // Create the scene
+ Scene = new UnityScene(map);
+ 
+ // Create the simulation
+ Simulation = new Simulation(Scene, MatchConfig);
+ 
+ // Initialize the simulation
+ Simulation.Initialize();
+ 
+ // Set the simulation state
+ IsRunning = true;
+ IsPaused = false;
+ CurrentStep = 0;
+ SimulationTime = 0;
+ 
+ // Raise the simulation started event
+ EventManager.Instance.RaiseEvent(new SimulationStartedEvent(Simulation.Id));
+ 
+ Debug.Log($"Simulation started: {MatchConfig.Name}");
+ }
+ 
+ /// <summary>
+ /// Pauses the simulation
+ /// </summary>
+ public void PauseSimulation()
+ {
+ if (!IsRunning || IsPaused)
+ return;
+ 
+ IsPaused = true;
+ 
+ // Raise the simulation paused event
+ EventManager.Instance.RaiseEvent(new SimulationPausedEvent(Simulation.Id));
+ 
+ Debug.Log("Simulation paused");
+ }
+ 
+ /// <summary>
+ /// Resumes the simulation
+ /// </summary>
+ public void ResumeSimulation()
+ {
+ if (!IsRunning || !IsPaused)
+ return;
+ 
+ IsPaused = false;
+ 
+ // Raise the simulation resumed event
+ EventManager.Instance.RaiseEvent(new SimulationResumedEvent(Simulation.Id));
+ 
+ Debug.Log("Simulation resumed");
+ }
+ 
+ /// <summary>
+ /// Stops the simulation
+ /// </summary>
+ public void StopSimulation()
+ {
+ if (!IsRunning)
+ return;
+ 
+ // Raise the simulation stopped event
+ EventManager.Instance.RaiseEvent(new SimulationStoppedEvent(Simulation.Id));
+ 
+ // Clean up the simulation
+ Simulation.Cleanup();
+ Simulation = null;
+ 
+ // Clear the entity dictionaries
+ _entities.Clear();
+ _characters.Clear();
+ _bullets.Clear();
+ 
+ // Set the simulation state
+ IsRunning = false;
+ IsPaused = false;
+ 
+ Debug.Log("Simulation stopped");
+ }
+ 
+ /// <summary>
+ /// Restarts the simulation
+ /// </summary>
+ public void RestartSimulation()
+ {
+ StopSimulation();
+ StartSimulation();
+ }
+ 
+ /// <summary>
+ /// Steps the simulation forward by one frame
+ /// </summary>
+ public void StepSimulation()
+ {
+ if (!IsRunning || !IsPaused)
+ return;
+ 
+ // Update the simulation
+ float deltaTime = Time.fixedDeltaTime * simulationSpeed;
+ Simulation.Update(deltaTime);
+ 
+ // Update the simulation time
+ SimulationTime += deltaTime;
+ 
+ // Increment the step counter
+ CurrentStep++;
+ 
+ // Raise the step completed event
+ EventManager.Instance.RaiseEvent(new SimulationStepCompletedEvent(Simulation.Id, CurrentStep, SimulationTime));
+ 
+ Debug.Log($"Simulation stepped: {CurrentStep}");
+ }
+ 
+ /// <summary>
+ /// Sets the simulation speed
+ /// </summary>
+ /// <param name="speed">New simulation speed</param>
+ public void SetSimulationSpeed(float speed)
+ {
+ simulationSpeed = Mathf.Max(0.1f, speed);
+ Debug.Log($"Simulation speed set to {simulationSpeed}x");
+ }
+ 
+ /// <summary>
+ /// Gets an entity by ID
+ /// </summary>
+ /// <param name="id">ID of the entity</param>
+ /// <returns>The entity with the specified ID, or null if not found</returns>
+ public Entity GetEntity(Guid id)
+ {
+ return _entities.TryGetValue(id, out var entity) ? entity : null;
+ }
+ 
+ /// <summary>
+ /// Gets a character by ID
+ /// </summary>
+ /// <param name="id">ID of the character</param>
+ /// <returns>The character with the specified ID, or null if not found</returns>
+ public Character GetCharacter(Guid id)
+ {
+ return _characters.TryGetValue(id, out var character) ? character : null;
+ }
+ 
+ /// <summary>
+ /// Gets a bullet by ID
+ /// </summary>
+ /// <param name="id">ID of the bullet</param>
+ /// <returns>The bullet with the specified ID, or null if not found</returns>
+ public Bullet GetBullet(Guid id)
+ {
+ return _bullets.TryGetValue(id, out var bullet) ? bullet : null;
+ }
+ 
+ /// <summary>
+ /// Subscribes to events
+ /// </summary>
+ private void SubscribeToEvents()
+ {
+ // Subscribe to entity events
+ eventReceiver.OnEntityCreated += OnEntityCreated;
+ eventReceiver.OnEntityRemoved += OnEntityRemoved;
+ 
+ // Subscribe to bullet events
+ eventReceiver.OnBulletFired += OnBulletFired;
+ eventReceiver.OnBulletDestroyed += OnBulletDestroyed;
+ }
+ 
+ /// <summary>
+ /// Unsubscribes from events
+ /// </summary>
+ private void UnsubscribeFromEvents()
+ {
+ // Unsubscribe from entity events
+ eventReceiver.OnEntityCreated -= OnEntityCreated;
+ eventReceiver.OnEntityRemoved -= OnEntityRemoved;
+ 
+ // Unsubscribe from bullet events
+ eventReceiver.OnBulletFired -= OnBulletFired;
+ eventReceiver.OnBulletDestroyed -= OnBulletDestroyed;
+ }
+ 
+ /// <summary>
+ /// Called when an entity is created
+ /// </summary>
+ /// <param name="sender">Sender of the event</param>
+ /// <param name="e">Event arguments</param>
+ private void OnEntityCreated(object sender, EntityCreatedEvent e)
+ {
+ // Add the entity to the dictionary
+ _entities[e.Entity.Id] = e.Entity;
+ 
+ // Add the entity to the appropriate dictionary
+ if (e.Entity is Character character)
+ {
+ _characters[e.Entity.Id] = character;
+ }
+ else if (e.Entity is Bullet bullet)
+ {
+ _bullets[e.Entity.Id] = bullet;
+ }
+ 
+ Debug.Log($"Entity created: {e.Entity.Name} ({e.Entity.Id})");
+ }
+ 
+ /// <summary>
+ /// Called when an entity is removed
+ /// </summary>
+ /// <param name="sender">Sender of the event</param>
+ /// <param name="e">Event arguments</param>
+ private void OnEntityRemoved(object sender, EntityRemovedEvent e)
+ {
+ // Remove the entity from the dictionary
+ _entities.Remove(e.Entity.Id);
+ 
+ // Remove the entity from the appropriate dictionary
+ if (e.Entity is Character)
+ {
+ _characters.Remove(e.Entity.Id);
+ }
+ else if (e.Entity is Bullet)
+ {
+ _bullets.Remove(e.Entity.Id);
+ }
+ 
+ Debug.Log($"Entity removed: {e.Entity.Name} ({e.Entity.Id})");
+ }
+ 
+ /// <summary>
